export default {
    props: {
        width: {
            type: Number,
            default: 500
        },
        height: {
            type: Number,
            default: 500
        },
        row: {
            type: Number,
            default: 3
        },
        col: {
            type: Number,
            default: 3
        },
        img: {
            type: String,
            required: true
        }
    },
    computed: {
        blockWidth () {
          return this.width / this.col;
        },
        blockHeight () {
            return this.height / this.row;
        },
        correctPoints () {
            const { row, col, blockWidth, blockHeight } = this;
            const arr = [];

            for (let i = 0; i< row; i ++) {
                for (let j = 0; j< col; j ++) {
                    arr.push({
                        x: j * blockWidth,
                        y: i * blockHeight,
                        id: new Date().getTime() + Math.random() * 100
                    })
                }
            }
            return arr;
        },
        blockPoints () {
            const points = this.correctPoints;
            const length = points.length;
            const lastEle = points[length - 1];
            const newArr = [...points];
            newArr.length = length - 1;
            newArr.sort(() => Math.random() - 0.5);
            newArr.push(lastEle);

            return newArr;

        }
    },
    methods: {
        handleClick (e) {
            const blockDom = e.target;
            const emptyDom = this.$refs.empty[0];

            if (!this.isAdjacent(blockDom, emptyDom)) {
                return;
            }
            const { left, top } = blockDom.style;

            blockDom.style.left = emptyDom.style.left;
            blockDom.style.top = emptyDom.style.top;
            emptyDom.style.left = left;
            emptyDom.style.top = top;
            const  winFlag = this.checkWin();
            if(winFlag) {
                this.winGame(emptyDom);
            }
        },
        isAdjacent (blockDom, emptyDom) {
            const { left:blockLeft, top:blockTop, width, height } = blockDom.style;
            const { left:emptyLeft, top:emptyTop } = emptyDom.style;
            const xDis = Math.floor(Math.abs(parseFloat(blockLeft) - parseFloat(emptyLeft))); //Math.floor()取整
            const yDis = Math.floor(Math.abs(parseFloat(blockTop) - parseFloat(emptyTop)));
            const flag = (blockLeft === emptyLeft && yDis === parseInt(height))
                            || (blockTop === emptyTop && xDis === parseInt(width));
            return flag;
        },
        checkWin() {
            const blockDomArr = this.$refs.block;
            // every()全真才为真
            return blockDomArr.every(dom => {
                const { left: blockLeft, top: blockTop } = dom.style;
                const { correctx: correctX, correcty: correctY } = dom.dataset;
                const  flag = parseInt(blockLeft) === parseInt(correctX) && parseInt(blockTop) === parseInt(correctY);
                return flag;
            })
        },
        winGame (emptyDom) {
            setTimeout(() => {
                alert('宗介！');
                emptyDom.style.opacity = 1;
               setTimeout(() => {
                   this.goToNextLevel();
               }, 300)
            }, 300)
        },
        goToNextLevel () {
            const answerFlag = window.confirm('要继续下一关吗？')
            if(answerFlag) {
                this.$emit('next');
            }
        }
    }
}
